<?php get_header(); ?>    <!-- main area -->    <article>      <div id="wrapper">      <!-- main -->      <div id="main">      <div class="center"><?php	$author = get_user_by( 'slug', get_query_var( 'author_name' ) );	global $my_host;	if($my_host == 'mcha.jp'){		$pickupStrings = "このライターの記事";	}else{		$pickupStrings = $author->data->display_name."'s Article";	}?>              <!-- author -->              <div class="pickup_tittle"><?php the_author_meta('display_name',$author->ID); ?></div>              <section>                <div class="author">                  <div class="author_img">                    <?php echo removeWidthHeight(get_avatar( get_the_author_meta('email',$author->ID), '96' )); ?>                  </div>                  <?php                  /*                  <div class="author_name">                    <a href="<?php the_author_meta('user_url',$author->ID); ?>"><?php the_author_meta('display_name',$author->ID); ?></a>                  </div>                  */                  ?>                                    <div class="author_text"><?php the_author_meta('description',$author->ID); ?>                     <div class="author_social">     <?php $authordesc = get_the_author_meta( 'facebook',    $author->ID ); if ( ! empty ( $authordesc ) ) { ?><a href="https://www.facebook.com/<?php the_author_meta('facebook', $author->ID); ?>" target="blank"><img src="<?php bloginfo('template_directory'); ?>/images/author/facebook.png"></a><?php } ?>     <?php $authordesc = get_the_author_meta( 'twitter',     $author->ID ); if ( ! empty ( $authordesc ) ) { ?><a href="http://www.twitter.com/<?php the_author_meta('twitter', $author->ID); ?>" target="blank"><img src="<?php bloginfo('template_directory'); ?>/images/author/twitter.png"></a><?php } ?>     <?php $authordesc = get_the_author_meta( 'google_plus', $author->ID ); if ( ! empty ( $authordesc ) ) { ?><a href="https://plus.google.com/<?php the_author_meta('google_plus', $author->ID); ?>" target="blank"><img src="<?php bloginfo('template_directory'); ?>/images/author/google.png"></a><?php } ?>                    </div>                  </div>                </div>                <div class="author_article"><?php echo $pickupStrings;?></div>              </section>              <!-- list -->              <section>                <div class="pickup"><?php	$paged = get_query_var('paged');	$args = '&posts_per_page=20&author='.$author->ID.'&paged='.$paged;	query_posts( $args );	// The Loop	while ( have_posts() ) : the_post();		$image_id = get_post_thumbnail_id();	$image_url = wp_get_attachment_image_src($image_id, true);	$category = get_the_category();	$cat_id   = $category[0]->cat_ID;	$cat_name = $category[0]->cat_name;?>                  <!-- 記事 -->                  <div class="pickup_box">                    <a href="<?php the_permalink(); ?>">                      <div class="pickup_category">                        <h3><?php echo $cat_name; ?> </h3>                      </div>                      <div class="pickup_image">                        <img src="<?php echo wp_get_attachment_url(get_post_thumbnail_id()); ?>">                      </div>                      <div class="pickup_text"><?php the_title(); ?>                        <p><?php the_excerpt(); ?></p>                      </div>                    </a>                    <!-- ライターリンクをaタグの外に出す -->                    <div class="pickup_written">                    <?php if(jpnCheck()) : ?>                        Written by <a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><?php the_author(); ?></a> on <?php the_time("Y/m/d"); ?>                    <?php else : ?>                        Translated by <a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><?php the_author(); ?></a> on <?php the_time("Y/m/d"); ?>                    <?php endif; ?>                    </div>                  </div>                  <!-- pickup_box_end -->                                    <?php	endwhile;	// Reset Query	wp_reset_query();?>                                                                                        </div>              </section><?php//Pagenation if (function_exists("pagination")) {	pagination($additional_loop->max_num_pages);}?>      </div>      </div><?php get_sidebar(); ?>    <article></article>    <!-- 無限読み込み開始 --><?php get_footer(); ?>