<?phpget_header();if($_GET['s']){	$words = $_GET['s'];}?>    <!-- main area -->    <article>      <!-- top image -->      <div class="container">      </div>      <div id="wrapper">      <!-- main -->      <div id="main">      <div class="center">      <section>        <!-- pick up -->        <div class="pickup">        <div class="pickup_tittle">Search results for "<?php echo $words; ?>"</div><?php	$i=0;	$args = "s=$words&posts_per_page=20";	$paged = get_query_var('paged');	if($paged){		$args = "s=$words&posts_per_page=20&paged=". $paged;	}	query_posts( $args );	// The Loop	while ( have_posts() ) : the_post();	$i++;	$image_id = get_post_thumbnail_id();	$image_url = wp_get_attachment_image_src($image_id, true);	$category = get_the_category();	$cat_id   = $category[0]->cat_ID;	$cat_name = $category[0]->cat_name;	?>	<!-- pickup_box -->	<div class="pickup_box">		<a href="<?php the_permalink(); ?>">			<div class="pickup_category" style="background-color: #ffab1a;">				<h3><?php echo $cat_name; ?> </h3>			</div>			<div class="pickup_image">				<img src="<?php echo wp_get_attachment_url(get_post_thumbnail_id()); ?>">			</div>			<div class="pickup_text">				<?php the_title(); ?>				<p><?php the_excerpt(); ?></p>			</div>		</a>		<!-- ライターリンクをaタグの外に出す -->		<div class="pickup_written">		<?php if(jpnCheck()) : ?>			Written by <a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><?php the_author(); ?></a> on <?php the_time("Y/m/d"); ?>		<?php else : ?>			Translated by <a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><?php the_author(); ?></a> on <?php the_time("Y/m/d"); ?>		<?php endif; ?>		</div>	</div><!-- pickup_box_end --><?php	endwhile;	// Reset Query	wp_reset_query();	if(!$i){		print "<br>Sorry, but nothing matched your search criteria. Please try again with some different keywords.";	}?>        </div>      </section><?php//Pagenation if (function_exists("pagination")) {	pagination($additional_loop->max_num_pages);}?>      </div>      </div><?php get_sidebar(); ?>    <article></article>    <!-- 無限読み込み開始 --><?php get_footer(); ?>